sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var parent = current.request_item;

	if (current.isNewRecord()) {
      setReqItemInformationNew();
	} else if (!current.isNewRecord() && current.u_task_stage.nil() && parent.u_task_stage.nil()) {
	  setReqItemInformationExist();
	}

    function setReqItemInformationNew() {
        //var parent = current.request_item;
		gs.addInfoMessage('test ' + parent.sys_class_name + ' stage ' + parent.u_task_stage); 
		gs.addInfoMessage(' variable RITM: ' + parent.variables.u_environment);
        if (parent.sys_class_name == 'sc_req_item') {
			if(current.u_task_stage.nil() && !parent.u_task_stage.nil()) {
				current.setValue('u_task_stage', parent.u_task_stage);
			} else if (current.u_task_stage.nil() && parent.u_task_stage.nil()) {
				current.setValue('u_task_stage', parent.variables.u_environment);
			}
        }
    }

	function setReqItemInformationExist() {
        //var parent = current.request_item;
	    gs.addInfoMessage('test ' + parent.sys_class_name + ' stage ' + parent.u_task_stage); 
	    gs.addInfoMessage(' variable RITM: ' + parent.variables.u_environment);
		gs.addInfoMessage('CATI Sys_id: ' + current.sys_id);
		gs.addInfoMessage('Task number: ' + current.number);
        if (parent.sys_class_name == 'sc_req_item') {
		    if(current.u_task_stage.nil() && parent.u_task_stage.nil()) {
			    current.setValue('u_task_stage', parent.variables.u_environment);
				var gr = new GlideRecord('sc_task');
				gr.addQuery('sys_id', current.sys_id);
				gr.query();
				if (gr.next()) {
				    gr.u_task_stage = parent.variables.u_environment;
				    gr.update();
				    gs.addInfoMessage("after: " + gr.getValue('number'));
			    }
		    }
        }
    }
})(current, previous);
